#!/bin/bash

if [ $# -ne 1 ]; then
    echo "Usage: $0 <URL>"
    exit 1
fi

url=$1

# Extract filename from URL
filename=$(basename "$url")

curl -o "$filename" "$url"

if [ $? -eq 0 ]; then
    echo "File '$filename' has been successfully saved."
else
    echo "Error downloading the file from '$url'."
fi

